var keypad = [
  [1,2,3],
  [4,5,6],
  [7,8,9]
]

var instructions = [
  "",
  "",
  "",
  "",
  ""
];

var parseInstruction = function(instruction, startingPoint) {
  var moves = instruction.split('');
  return moves.reduce(function(acc, cur){
    var row = Math.min(2, Math.max(0, acc.row + ((cur === 'U') ? -1 : ((cur === 'D') ? 1 : 0))));
    var col = Math.min(2, Math.max(0, acc.col + ((cur === 'L') ? -1 : ((cur === 'R') ? 1 : 0))));
    return { row: row, col: col };
  }, startingPoint);
}

var start = { row: 1, col: 1 };

var code = instructions.reduce(function(acc, instruction){
  var temp = parseInstruction(instruction, start);
  start = temp;
  return acc + keypad[temp.row][temp.col];
}, '');

console.log(code); // 19636
